INSERT INTO `anime` (`name_anime`, `name_auteur`,`genre_anime`,`type_anime`, `description_anime`, `mark_anime`,`id`) VALUES
('one piece', 'Eiichiro Oda','cambat', 'voyage' ,'Luffy part à laventure après sa rencontre avec Shanks le Roux, le capitaine dun navire de pirates qui a passé un an dans son village et la sauvé dun monstre marin en sacrifiant son bras gauche. Depuis, Luffy porte son chapeau de paille quil lui a offert pour marquer la promesse de devenir un grand pirate. Ce chapeau deviendra donc le symbole de son équipage. Cest à cette époque quil mange un fruit du démon que détenait Shanks : le fruit du Gum Gum, et qui rend son corps élastique. Les fruits du Démon une fois mangés donnent des capacités spéciales à leur détenteur, qui perd par la même occasion toutes ses forces lorsquil est immergé dans leau de mer. Luffy et son équipage feront de nombreuses rencontres qui renforceront leur amitié et élargiront leurs équipages. Mais ils devront se confronter aux équipages pirates prônant violence et pouvoirs, ainsi quà la Marine et ses soldats, garants de la justice.' ,'5', 1),
('demon slayer','Koyoharu Gotoge', 'combat', 'Action', 'Protagoniste de lhistoire au cheveux et yeux de feu, avec des cartes en guise de boucles doreille et une tache de naissance du côté gauche du front, il est le premier enfant dun vendeur de charbon de bois, vivant en montagne avec sa mère et sa fratrie dont il prend soin du mieux quil peut depuis la mort de leur père. À lexception de sa cadette Nezuko, la fille aînée qui sest changée en démon, toute sa famille a été massacrée par un démon alors quil sétait absenté pour vendre du charbon de bois dans la ville den bas ; il voyage depuis lors avec sa sœur à la recherche dun antidote pouvant la faire redevenir humaine.','5',2),
('Beastars', 'Paru Itagaki', 'romance ','drame', 'Lhistoire se déroule dans un monde danimaux anthropomorphes civilisés avec une fracture culturelle entre les carnivores et les herbivores. La série tire son nom du rang dans lunivers de la série « Beastar », un individu au grand talent, au service et à la notoriété. Legoshi, un grand loup gris, est un étudiant timide et calme de linstitut Cherryton où il vit dans un dortoir avec plusieurs autres étudiants carnivores, dont son ami extraverti Jack, un golden retriever. En tant que membre du club de théâtre de lécole, Legoshi en est le machiniste et soutient les acteurs du club dirigé par lélève vedette Louis, un cerf rouge. Mais soudainement, Tem lalpaga est brutalement assassiné et dévoré dans la nuit, provoquant une vague de malaise et de méfiance entre les élèves herbivores et carnivores. Au même moment, Legoshi fait la rencontre fatidique de Haru, une petite lapine naine blanche, et commence à développer des sentiments complexes pour cette dernière.','5',3);
INSERT INTO `season` (`name_season`, `story_arc`, `description_arc`, `id`,`id_anime`) VALUES
('one piece saison1', 'east blue', 'Dix ans après avoir dévoré lun des Fruits du Démon, le Gomu Gomu no Mi, qui le rend élastique, Luffy part de son village pour se constituer un équipage et trouver le One Piece, trésor caché du Roi des Pirates.', 1,1),
('demon salyer saison 1', 'Demon Slayer 1','Découvrez une aventure où le sang va couler sous la lame de l épée. Nous sommes pendant l ère Taishô. Tanjirô, un jeune et sympathique vendeur de charbon voit son quotidien changer radicalement après le massacre de sa famille par un démon. Seule sa petite sœur Nezuko survit à l attaque, mais elle est transformée en un démon féroce. Ils partent tous deux en voyage afin de prendre leur revanche et de rendre à Nezuko son apparence d’origine. Vivez avec eux ce périple dans lequel le destin des humains et des démons s entremêlent.', 2,2),
('beastars saison1', 'beastars 1','Lhistoire se déroule dans un monde danimaux anthropomorphes civilisés avec une fracture culturelle entre les carnivores et les herbivores. La série tire son nom du rang dans lunivers de la série Beastar, un individu au grand talent, au service et à la notoriété. Legoshi, un grand loup gris, est un étudiant timide et calme de linstitut Cherryton où il vit dans un dortoir avec plusieurs autres étudiants carnivores, dont son ami extraverti Jack, un golden retriever. En tant que membre du club de théâtre de lécole, Legoshi en est le machiniste et soutient les acteurs du club dirigé par lélève vedette Louis, un cerf rouge. Mais soudainement, Tem lalpaga est brutalement assassiné et dévoré dans la nuit, provoquant une vague de malaise et de méfiance entre les élèves herbivores et carnivores. Au même moment, Legoshi fait la rencontre fatidique de Haru, une petite lapine naine blanche, et commence à développer des sentiments complexes pour cette dernière.', 3,3);
INSERT INTO `episode` (`number_episode`, `name_episode`, `time_episode`, `description_episode`, `id`, `mark_episode`) VALUES
('1','JE SUIS LUFFY ! CELUI QUI DEVIENDRA ROI DES PIRATES !', '23','Oyez ! Oyez ! Pirates de tous bords ! Gold Roger, le légendaire Roi des pirates, mort sur l’échafaud, a laissé derrière lui un trésor inestimable : le One Piece . Monkey D. Luffy, un garçon naïf et impulsif, rêve de mettre la main sur ce fameux butin et de devenir le nouveau Roi des Pirates. Transformé en homme-élastique après avoir avalé un des fruits du démon, Luffy part en quête d’un équipage digne de son ambition ! A l’abordage !', 1, '5'),
('1','DEMON SLAYER: KIMETSU NO YAIBA', '24',' Nous sommes pendant l’ère Taishô. Tanjirô, un jeune et sympathique vendeur de charbon voit son quotidien changer radicalement après le massacre de sa famille par un démon. Seule sa petite sœur Nezuko survit à l’attaque, mais elle est transformée en un démon féroce. Ils partent tous deux en voyage afin de prendre leur revanche et de rendre à Nezuko son apparence d’origine. Vivez avec eux ce périple dans lequel le destin des humains et des démons s’entremêlent.',2,'5' ),
('1','La lune et la bête','25','Le meurtre d un herbivore chamboule l Institut Cherrygton',3,'5' );